mapper.select:
SELECT DISTINCT mountain_name, m.mountain_num, mountain_chinese, mountain_subtitle, mountain_location, mountain_manage, height, main_content, sub_content, mountain_img FROM mountain m JOIN mountain_addr ad ON m.mountain_num = ad.mountain_num JOIN sigungoo si ON ad.sigungoo_num = si.sigungoo_num JOIN sido do ON si.sido_num = do.sido_num
AND sido_name = #{sido_name}
AND sigungoo_name = #{sigungoo_name}
ORDER BY mountain_name ASC OFFSET #{offset} ROWS FETCH FIRST #{size} ROWS ONLY

SELECT NVL(COUNT(DISTINCT m.mountain_num), 0) FROM mountain m JOIN mountain_addr ad ON m.mountain_num = ad.mountain_num JOIN sigungoo si ON ad.sigungoo_num = si.sigungoo_num JOIN sido do ON si.sido_num = do.sido_num
AND sido_name = #{sido_name}
AND sigungoo_name = #{sigungoo_name}
